import React, { Component }  from 'react';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

class Add extends Component {
    
    constructor(props){
        super(props)
        this.state = {   
        id: '',
        customer: '',
        destination: '',
        driver: '',
        deliveryDate: '',
        } 
        
        this.handleChange = this.handleChange.bind(this);
    }
   

    handleChange(event){
        const { name, value} = event.target
        this.setState({[name]: value,});
        console.log(this.state);
    }
  
    
    render() {
        return (
        <div className="add-form">
            <Form >
                <Form.Group >
                    <Form.Label>Customer</Form.Label>
                    <Form.Control type="text" name="customer" id="customer" onChange={this.handleChange} />
                    <Form.Label>Destination</Form.Label>
                    <Form.Control type="text"  name="destination" id="destination" onChange={this.handleChange} />
                    <Form.Label>Driver</Form.Label>
                    <Form.Control type="text"  name="driver" id="driver" onChange={this.handleChange} />
                    <Form.Label>Date</Form.Label>
                    <Form.Control type="text"name="deliveryDate" id="deliveryDate" onChange={this.handleChange}/>
                </Form.Group>
                <Button variant="success" className="add-button1">Add Load</Button>
            </Form>
        </div>
        );
    }
}

