import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button}  from 'react-bootstrap';





class Home extends Component {
    async componentDidMount(){
        const response=await fetch('/')
        const body = await response.json();
        this.setState({loads :body});
    }
 
    render() {
    let renderLoads = (load, loadNumber) => {
        return (
            <tr key = {loadNumber}>
                <td>{load.loadNumber}</td>
                <td>{load.customer}</td>
                <td>{load.destination}</td>
                <td>{load.driver}</td>
                <td>{load.deliveryDate}</td>
                <td><Button variant="outline-danger">Delete</Button></td>
            </tr>
        )
    }
    
   
    <div >
    <Table striped bordered hover className="scrollTable">
    <thead>
        <tr>
        <th>Load #</th>
        <th>Customer</th>
        <th>Destination</th>
        <th>Driver</th>
        <th>Delivery Date</th>
        <th>Edit</th>
        </tr>
    </thead>
    <tbody>
        {loads.map(renderLoads)}
    </tbody>
    </Table>
    <Button variant="outline-success" className="add-button" href="/add">Add Load</Button>
    </div>
    }
}

export default Home;
