import React, { Component }  from 'react';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

class Add extends Component {
    
    constructor(props){
        super(props)
        this.state = {   
        id: '',
        customer: '',
        destination: '',
        driver: '',
        deliveryDate: ''} 
        // this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    
    // async handleSubmit(event){
    //     const {load} = this.state;
    //     await fetch('/add', {
    //         method : 'POST',
    //         headers : {
    //             'Accept' : 'application/json',
    //             'Content-Type' : 'application/json'
    //             },
    //         body : JSON.stringify(load),
    //         })
    //     this.props.history.push('/');
        
    // }

    handleChange(event){
        this.setState({load: event.target.value});
        console.log(this.state.load);
    }
  
    
    render() {
        return (
        <div className="add-form">
            <Form >
                <Form.Group >
                    <Form.Label>Customer</Form.Label>
                    <Form.Control type="text" name="customer" id="customer" onChange={this.handleChange} />
                    <Form.Label>Destination</Form.Label>
                    <Form.Control type="text"  name="destination" id="destination" onChange={this.handleChange} />
                    <Form.Label>Driver</Form.Label>
                    <Form.Control type="text"  name="driver" id="driver" onChange={this.handleChange} />
                    <Form.Label>Date</Form.Label>
                    <Form.Control type="text"name="deliveryDate" id="deliveryDate" onChange={this.handleChange}/>
                </Form.Group>
                <Button variant="success" className="add-button1">Add Load</Button>
            </Form>
        </div>
        );
    }
}
  
  export default Add;
