import React, { Component }  from 'react';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

class Add extends Component {
    emptyLoad =  {
        id: '',
        customer: '',
        destination: '',
        driver: '',
        date: ''
    }
    
    constructor(props){
        super(props)
        this.state = {load : this.emptyLoad} 
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    
    async handleSubmit(event){
        event.preventDefault();
        const {item} = this.state;
        await fetch('/add', {
            method : 'POST',
            headers : {
                'Accept' : 'application/json',
                'Content-Type' : 'application/json'
                },
            body : JSON.stringify(item),
            })
        this.props.history.push('/');
        
    }

    handleChange(event){
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let load= {...this.state.load};
        load[name] = value;
        this.setState({load});
        console.log(this.state);
    }
  
    
    render() {
        return (
        <div className="add-form">
            <Form onSubmit={this.handleSubmit}>
                <Form.Group >
                    <Form.Label>Customer</Form.Label>
                    <Form.Control type="text" name="customer" id="customer" onChange={this.handleChange} />
                    <Form.Label>Destination</Form.Label>
                    <Form.Control type="text"  name="destination" id="destination" onChange={this.handleChange} />
                    <Form.Label>Driver</Form.Label>
                    <Form.Control type="text"  name="driver" id="driver" onChange={this.handleChange} />
                    <Form.Label>Date</Form.Label>
                    <Form.Control type="text" onChange={this.handleChange}/>
                </Form.Group>
                <Button variant="success" className="add-button1">Add Load</Button>
            </Form>
        </div>
        );
    }
}
  
  export default Add;
