import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button}  from 'react-bootstrap';





function Home() {
    let loads = [
        {loadNumber: 1, customer: "Home Depot", destination: "Chicago", driver: "Steve", deliveryDate: "04/15/20"},
        {loadNumber: 2, customer: "Schnucks", destination: "Kansas City", driver: "Geoff", deliveryDate: "04/10/20"},
        {loadNumber: 3, customer: "Weekends Only", destination: "Nashville", driver: "Sandy", deliveryDate: "04/07/20"},
        {loadNumber: 4, customer: "Wal Mart", destination: "Cleveland", driver: "Mike", deliveryDate: "04/08/20"},
        {loadNumber: 5, customer: "Costco", destination: "St. Louis", driver: "Jamal", deliveryDate: "04/06/20"},
        {loadNumber: 6, customer: "Sams", destination: "Atlanta", driver: "Sameer", deliveryDate: "04/11/20"},
        {loadNumber: 7, customer: "CVS", destination: "Dallas", driver: "Tanya", deliveryDate: "04/18/20"},
        {loadNumber: 8, customer: "Ford", destination: "Detroit", driver: "Viktor", deliveryDate: "04/05/20"},
        {loadNumber: 9, customer: "Dicks Sporting Goods", destination: "Louisville", driver: "Rajesh", deliveryDate: "04/12/20"},
        {loadNumber: 10, customer: "Pfizer", destination: "Tampa Bay", driver: "Howad", deliveryDate: "04/25/20"}
    ]

    let renderLoads = (load, loadNumber) => {
        return (
            <tr key = {loadNumber}>
                <td>{load.loadNumber}</td>
                <td>{load.customer}</td>
                <td>{load.destination}</td>
                <td>{load.driver}</td>
                <td>{load.deliveryDate}</td>
                <td><Button variant="danger">Delete</Button></td>
            </tr>
        )
    }
    return (
   
    <div className="scrollTable">
    <Table striped bordered hover size="sm" height='500px' scrollY>
    <thead>
        <tr>
        <th>Load #</th>
        <th>Customer</th>
        <th>Destination</th>
        <th>Driver</th>
        <th>Delivery Date</th>
        <th>Delete</th>
        </tr>
    </thead>
    <tbody>
        {loads.map(renderLoads)}
    </tbody>
    </Table>
    </div>
 );
}

export default Home;
